// import { useEffect, useState } from "react";
const api_url = `https://api.adviceslip.com/advice`;

function App() {
  const getData = async () => {
    let response = await fetch(api_url);
    let data = await response.json();
    let advice = data.slip.advice;
    console.log(advice);
  };
  getData();
}

export default App;
