function Todo
    ({todoText :todoText,  fnc : deleteTodo , id: todoId})
    {
    let [todosLS, setTodosLS] = useState(savedTodos)

    return (
        <div className="todo" key={todoId}>
            <span className="todo-text">
            {todoText}
            </span>
            <span className="todo-tools">
                <span onClick=
                    { () => {
                    deleteTodo(todoId);
                    setTodosLS(savedTodos)
                    } }
                    className="todo-btn todo-delete">x</span>
            </span>
        </div>
    )
}
export default Todo
