// import { useState, useEffect } from "react";
import Header from "./Header"
import Todos from "./Todos";
import AddTodo from "./AddTodo"
let todos = [
  {todoId : 0, todoText : "psdsdsddsdlay with kids"},
  {todoId : 1, todoText : "learn morrocan"},
  {todoId : 2, todoText : "look for mother"},
]

function Container() {
  function modeSwitch() {
    let bgc = document.body.style.backgroundColor
    let clr = document.body.style.color
    if (bgc === "black" && clr === "white") {
        document.body.style.backgroundColor = "white"
        document.body.style.color = "black"
    }
    else if (bgc === "white" && clr === "black"){
        document.body.style.backgroundColor = "black"
        document.body.style.color = "white"
    }
    else {
      document.body.style.backgroundColor = "black"
      document.body.style.color = "white"
    }
  }
  return (
    <div className="container">
    <Header modeSwitch={modeSwitch}/>
    <AddTodo/>
    <Todos todos={todos}/>
  </div>
  )
}

export default Container;
