let todos = [
    {todoId : 0, todoText : "play with kids"},
    {todoId : 1, todoText : "learn morrocan"},
    {todoId : 2, todoText : "look for mother"}]

function todoAdd() {
    let inp = document.querySelector(".input")
    // if no todos exist
    let savedTodos = JSON.parse(localStorage.getItem("todos"))
    if (savedTodos) {
        savedTodos.push({todoId : savedTodos.length, todoText : inp.value})
        localStorage.setItem("todos", JSON.stringify(savedTodos))
    }
    // if todos are in Local Storage
    else {
        localStorage.setItem("todos", JSON.stringify([
            {todoId : 0, todoText : inp.value}
        ]))
    }
}

export {todoAdd, todos}
