import {useState, useEffect} from "react"
let [todosLS, setTodosLS] = useState(savedTodos)
// export default {Todos }

function Todos ({fnc: deleteTodo}) {
    let savedTodos = JSON.parse(localStorage.getItem("todos"))
    useEffect (() => {
    })
    return (
    <div className="todos">
    { todosLS.map( (todo) => (
        <Todo todoText={todo.todoText}
         fnc={deleteTodo} key={todo.todoId} id={todo.todoId}/>
        )) }
    </div>
    )
}
export default {Todos }
