import Todo from "./Todo"
import {useState, useEffect} from "react"

function Todos () {
    let savedTodos = JSON.parse(localStorage.getItem("todos"))
    let [todosLS, setTodosLS] = useState(savedTodos)
    console.log(todosLS)
    useEffect (() => {
        return (
            <div className="todos">
            { todosLS.map( (todo) => (
                <Todo todoText={todo.todoText}
                key={todo.todoId} />
                )) }
            </div>
            )
    })
    
}
export default Todos
