const api_key = "cad15e9b";
const api_url = `http://www.omdbapi.com/?apikey=${api_key}`;

let displayMovies = async function (movName) {
  let response = await fetch(api_url + "&s=" + movName);
  let data = await response.json();
  if (data.Response) {
    let ex = data.Search[0];
    return (
      <div>
        <p>movie name : {ex.Title}</p>
        <img src={ex.Poster} />
      </div>
    );
  } else {
    return <h4>no Movies found</h4>;
  }
};
displayMovies("Batman");
export default displayMovies;
