import { useState } from "react";
function App() {
  return (
    <>
      <input />
      <button onClick={addText}>add text</button>
      <div className="res"></div>
    </>
  );
}
const [text, setText] = useState("");
const addText = () => {
  let inp = document.querySelector("input");
  setText(inp.value);
  let div = document.querySelector(".res");
  div.innerHTML += text;
};

export default App;
